		vst_shopping_live_order_date >= #{startDate}
	
	
		AND vst_shopping_live_order_date < #{endDate}
	
	
		AND FROM_UNIXTIME(vst_shopping_live_order_date / 1000, '%H:%i:%S') >= #{startTime}
	
	
		AND FROM_UNIXTIME(vst_shopping_live_order_date / 1000, '%H:%i:%S') <= #{endTime}
	
	
		AND vst_shopping_live_order_channel = #{vst_shopping_live_order_channel}
	
	
		AND vst_shopping_live_order_number = #{vst_shopping_live_order_number}
	
	
		AND vst_shopping_live_order_goods_number = #{vst_shopping_live_order_goods_number}
	
	
		AND vst_shopping_live_order_goods_category like '%${vst_shopping_live_order_goods_category}%'
	
	
		AND vst_shopping_live_order_goods_name like '%${vst_shopping_live_order_goods_name}%'
	
	
		AND vst_shopping_live_order_province like '%${vst_shopping_live_order_province}%'
	
	
		AND vst_shopping_live_order_address like '%${vst_shopping_live_order_address}%'
	
	
		AND vst_shopping_live_order_recipient like '%${vst_shopping_live_order_recipient}%'
	
	
		AND vst_shopping_live_order_phone like '%${vst_shopping_live_order_phone}%'
	
	
		AND vst_shopping_live_order_source like '%${vst_shopping_live_order_source}%'
	
	
		AND vst_shopping_live_order_balance_status = #{vst_shopping_live_order_balance_status}
	
	
		AND vst_shopping_live_order_logistics_status = #{vst_shopping_live_order_logistics_status}
	
	
		AND vst_shopping_live_order_operator like '%${vst_shopping_live_order_operator}%'
	

ORDER BY

	
		vst_shopping_live_order_date DESC
	
	
		${orderBy} ${order}
	


	LIMIT #{limit}


	OFFSET #{offset}

