		AND vst_shopping_live_order_date < #{endDate}
	
	
		AND FROM_UNIXTIME(vst_shopping_live_order_date / 1000, '%H:%i:%S') >= #{startTime}
	
	
		AND FROM_UNIXTIME(vst_shopping_live_order_date / 1000, '%H:%i:%S') <= #{endTime}
	
	
