mapper.select:
SELECT * FROM vst_shopping_play_order

	
		vst_shopping_play_order_date >= #{startDate}
	
	
		AND vst_shopping_play_order_date < #{endDate}
	
	
		AND vst_shopping_play_order_channel = #{vst_shopping_play_order_channel}
	
	
		AND vst_shopping_play_order_supplier_type = #{vst_shopping_play_order_supplier_type}
	
	
		AND vst_shopping_play_order_good_id = #{vst_shopping_play_order_good_id}
	
	
		AND vst_shopping_play_order_good_name like '%${vst_shopping_play_order_good_name}%'
	
	
		AND vst_shopping_play_order_province like '%${vst_shopping_play_order_province}%'
	
	
		AND vst_shopping_play_order_address like '%${vst_shopping_play_order_address}%'
	
	
		AND vst_shopping_play_order_recipient like '%${vst_shopping_play_order_recipient}%'
	
	
		AND vst_shopping_play_order_phone like '%${vst_shopping_play_order_phone}%'
	
	
		AND vst_shopping_play_order_operator like '%${vst_shopping_play_order_operator}%'
	

ORDER BY

	
		vst_shopping_play_order_date DESC
	
	
		${orderBy} ${order}
	


	LIMIT #{limit}


	OFFSET #{offset}

