mapper.select:
SELECT

	
		${export_column}
	
	
		vst_shopping_play_order_date '日期', vst_shopping_play_order_channel '渠道', vst_shopping_play_order_supplier_type '供应商类型',
		vst_shopping_play_order_good_id '商品编号', vst_shopping_play_order_good_name '商品名称', vst_shopping_play_order_good_price '商品价格',
		vst_shopping_play_order_qty '购买数量', vst_shopping_play_order_sale_price '销售金额', vst_shopping_play_order_province '省份',
		vst_shopping_play_order_address '收件地址', vst_shopping_play_order_recipient '收件人', vst_shopping_play_order_phone '联系电话'
	

FROM vst_shopping_play_order

	
		vst_shopping_play_order_date >= #{startDate}
	
	
		AND vst_shopping_play_order_date < #{endDate}
	
	
		AND vst_shopping_play_order_channel = #{vst_shopping_play_order_channel}
	
	
		AND vst_shopping_play_order_supplier_type = #{vst_shopping_play_order_supplier_type}
	
	
		AND vst_shopping_play_order_good_id = #{vst_shopping_play_order_good_id}
	
	
		AND vst_shopping_play_order_good_name like '%${vst_shopping_play_order_good_name}%'
	
	
		AND vst_shopping_play_order_province like '%${vst_shopping_play_order_province}%'
	
	
		AND vst_shopping_play_order_address like '%${vst_shopping_play_order_address}%'
	
	
		AND vst_shopping_play_order_recipient like '%${vst_shopping_play_order_recipient}%'
	
	
		AND vst_shopping_play_order_phone like '%${vst_shopping_play_order_phone}%'
	
	
		AND vst_shopping_play_order_operator like '%${vst_shopping_play_order_operator}%'
	

ORDER BY

	
		vst_shopping_play_order_date DESC
	
	
		${orderBy} ${order}

SELECT
	vst_shopping_play_order_date as date,
	vst_shopping_play_order_channel as channel,
	vst_shopping_play_order_good_price as goodPrice,
	vst_shopping_play_order_qty as qty,
	vst_shopping_play_order_sale_price as salePrice
FROM vst_shopping_play_order

	
		vst_shopping_play_order_date >= #{startDate}
	
	
		AND vst_shopping_play_order_date < #{endDate}
	
	
		AND vst_shopping_play_order_channel = #{vst_shopping_play_order_channel}
	
	
		AND vst_shopping_play_order_channel != #{vst_shopping_play_order_channel_ne}
	
	
		AND vst_shopping_play_order_supplier_type = #{vst_shopping_play_order_supplier_type}
	
	
		AND vst_shopping_play_order_good_id = #{vst_shopping_play_order_good_id}
	
	
		AND vst_shopping_play_order_good_name like '%${vst_shopping_play_order_good_name}%'
	
	
		AND vst_shopping_play_order_province like '%${vst_shopping_play_order_province}%'
	
	
		AND vst_shopping_play_order_address like '%${vst_shopping_play_order_address}%'
	
	
		AND vst_shopping_play_order_recipient like '%${vst_shopping_play_order_recipient}%'
	
	
		AND vst_shopping_play_order_phone like '%${vst_shopping_play_order_phone}%'
	
	
		AND vst_shopping_play_order_operator like '%${vst_shopping_play_order_operator}%'
	

ORDER BY vst_shopping_play_order_date ASC

SELECT
	vst_shopping_play_order_good_id as goodId,
	vst_shopping_play_order_good_name as goodName,
	count(1) as num,
	sum(vst_shopping_play_order_qty) as qty,
	sum(vst_shopping_play_order_sale_price) as salePrice
FROM vst_shopping_play_order

	
		vst_shopping_play_order_date >= #{startDate}
	
	
		AND vst_shopping_play_order_date < #{endDate}
	
	
		AND vst_shopping_play_order_channel = #{vst_shopping_play_order_channel}
	
	
		AND vst_shopping_play_order_supplier_type = #{vst_shopping_play_order_supplier_type}
	
	
		AND vst_shopping_play_order_good_id = #{vst_shopping_play_order_good_id}
	
	
		AND vst_shopping_play_order_good_name like '%${vst_shopping_play_order_good_name}%'
	
	
		AND vst_shopping_play_order_province like '%${vst_shopping_play_order_province}%'
	
	
		AND vst_shopping_play_order_address like '%${vst_shopping_play_order_address}%'
	
	
		AND vst_shopping_play_order_recipient like '%${vst_shopping_play_order_recipient}%'
	
	
		AND vst_shopping_play_order_phone like '%${vst_shopping_play_order_phone}%'
	
	
		AND vst_shopping_play_order_operator like '%${vst_shopping_play_order_operator}%'
	

GROUP BY vst_shopping_play_order_good_id, vst_shopping_play_order_good_name
ORDER BY

	
		num DESC, qty DESC, salePrice DESC
	
	
		${orderBy} ${order}
	


	LIMIT #{limit}


	OFFSET #{offset}

SELECT count(1) FROM (
	SELECT
		vst_shopping_play_order_good_id as goodId,
		vst_shopping_play_order_good_name as goodName
	FROM vst_shopping_play_order
	
		
			vst_shopping_play_order_date >= #{startDate}
		
		
			AND vst_shopping_play_order_date < #{endDate}
		
		
			AND vst_shopping_play_order_channel = #{vst_shopping_play_order_channel}
		
		
			AND vst_shopping_play_order_supplier_type = #{vst_shopping_play_order_supplier_type}
		
		
			AND vst_shopping_play_order_good_id = #{vst_shopping_play_order_good_id}
		
		
			AND vst_shopping_play_order_good_name like '%${vst_shopping_play_order_good_name}%'
		
		
			AND vst_shopping_play_order_province like '%${vst_shopping_play_order_province}%'
		
		
			AND vst_shopping_play_order_address like '%${vst_shopping_play_order_address}%'
		
		
			AND vst_shopping_play_order_recipient like '%${vst_shopping_play_order_recipient}%'
		
		
			AND vst_shopping_play_order_phone like '%${vst_shopping_play_order_phone}%'
		
		
			AND vst_shopping_play_order_operator like '%${vst_shopping_play_order_operator}%'
		
	
	GROUP BY goodId, goodName
) a

SELECT vst_shopping_play_order_good_id '商品编号', vst_shopping_play_order_good_name '商品名称', count(1) '订单数量', sum(vst_shopping_play_order_qty) '购买数量', sum(vst_shopping_play_order_sale_price) '销售总金额' FROM vst_shopping_play_order
